from pathlib import Path
from langchain_community.document_loaders import PyMuPDFLoader

docs = []

for document in Path("/content/documents/").glob("*.pdf"):
  try:
    print(f'Carregando arquivo: "{document.name}"')
    loader = PyMuPDFLoader(str(document))
    docs.extend(loader.load())
    print(f'‚úÖ Arquivo carregado com sucesso')
  except Exception as e:
    print(f'‚ùå Erro ao carregar arquivo \n{e}')
  print(f'------------------------------------------------------------------\n')

print(f'üóÇÔ∏è Total de documentos carregadas: {len(docs)}')

"""Vamos importar a biblioteca que ser√° respons√°vel por quebrar o documento em pequenos peda√µs para responder melhor uma pergunta do usu√°rio"""

from langchain_text_splitters import RecursiveCharacterTextSplitter

splitter = RecursiveCharacterTextSplitter(chunk_size=300, chunk_overlap=30)

chunks = splitter.split_documents(docs)

chunks

for chunk in chunks:
  print(chunk)
  print("--------------------------")

"""Carregando o Google Gemini Embeddings"""

from langchain_google_genai import GoogleGenerativeAIEmbeddings

embeddings = GoogleGenerativeAIEmbeddings(
    model = "models/gemini-embedding-001",
    google_api_key = GOOGLE_API_KEY
)

"""Importando a biblioteca FAISS (Facebook A.I. Similarity Search), que instalamos anteriormente, para ajudar a gente √† realizar uma busca mais eficiente nos"""

from langchain_community.vectorstores import FAISS

vectorstore = FAISS.from_documents(chunks, embeddings)

retriever = vectorstore.as_retriever(search_type="similarity_score_threshold",
                                     search_kwargs={"score_threshold":0.3, "k": 4})

from langchain_core.prompts import ChatPromptTemplate
from langchain.chains.combine_documents import create_stuff_documents_chain

prompt_rag = ChatPromptTemplate.from_messages([
    ("system",
     "Voc√™ √© um Assistente de Pol√≠ticas Internas (RH/IT) da empresa Carraro Desenvolvimento. "
     "Responda SOMENTE com base no contexto fornecido. "
     "Se n√£o houver base suficiente, responda apenas 'N√£o sei'."),

    ("human", "Pergunta: {input}\n\nContexto:\n{context}")
])

document_chain = create_stuff_documents_chain(llm_triagem, prompt_rag)

"""Conectando tudo o que foi feito at√© agora"""

def perguntar_politica_RAG(pergunta: str) -> Dict:
  docs_relacionados = retriever.invoke(pergunta)

  if not docs_relacionados:
    return {
        "answer": "N√£o sei",
        "citacoes": [],
        "contexto_encontrado": False
    }

  answer = document_chain.invoke({
      "input": pergunta,
      "context": docs_relacionados
  })

  txt = (answer or "").strip()

  if txt.rstrip(".!?") == "N√£o sei":
    return {
        "answer": "N√£o sei",
        "citacoes": [],
        "contexto_encontrado": False
    }

  return {
      "answer": txt,
      "citacoes": docs_relacionados,
      "contexto_encontrado": True
  }

