"""Criando o nosso agente"""

from typing import TypedDict, Optional

class AgentState(TypedDict, total=False):
  mensagem: str
  triagem: dict
  resposta: Optional[str]
  citacoes: List[dict]
  rag_sucesso: bool
  acao_final: str

def node_triagem(state: AgentState) -> AgentState:
  print("Executando n√≥ de triagem>...")
  return {"triagem": triagem(state["mensagem"])}

def node_auto_resolver(state: AgentState) -> AgentState:
  print("Executando n√≥ de auto resolu√ß√£o")
  # return {"acao_final": "AUTO_RESOLVER"} # This line was causing the issue

  resposta_rag = perguntar_politica_RAG(state["mensagem"]) # Call the RAG function

  update: AgentState = {
      "resposta": resposta_rag["answer"],
      "citacoes": resposta_rag["citacoes"], # Corrected key name
      "rag_sucesso": resposta_rag["contexto_encontrado"]
  }

  if resposta_rag["contexto_encontrado"]:
      update["acao_final"] = "AUTO_RESOLVER"
  else: # Added else case to handle when context is not found
      update["acao_final"] = "PEDIR_INFO" # Default to PEDIR_INFO if no context found

  return update

def node_pedir_info(state: AgentState) -> AgentState:
    print("Executando n√≥ de pedir_info...")
    faltantes = state["triagem"].get("campos_faltantes", [])
    if faltantes:
        detalhe = ",".join(faltantes)
    else:
        detalhe = "Tema e contexto espec√≠fico"

    return {
        "resposta": f"Para avan√ßar, preciso que detalhe: {detalhe}",
        "citacoes": [],
        "acao_final": "PEDIR_INFO"
    }

def node_abrir_chamado(state: AgentState) -> AgentState:
    print("Executando n√≥ de abrir_chamado...")
    triagem = state["triagem"]

    return {
        "resposta": f"Abrindo chamado com urg√™ncia {triagem['urgencia']}. Descri√ß√£o: {state['pergunta'][:140]}",
        "citacoes": [],
        "acao_final": "ABRIR_CHAMADO"
    }

KEYWORDS_ABRIR_TICKET = ["aprova√ß√£o", "exce√ß√£o", "libera√ß√£o", "abrir ticket", "abrir chamado", "acesso especial"]

def decidir_pos_triagem(state: AgentState) -> str:
    print("Decidindo ap√≥s a triagem...")
    decisao = state["triagem"]["decisao"]

    if decisao == "AUTO_RESOLVER": return "auto"
    if decisao == "PEDIR_INFO": return "info"
    if decisao == "ABRIR_CHAMADO": return "chamado"

def decidir_pos_auto_resolver(state: AgentState) -> str:
    print("Decidindo ap√≥s o auto_resolver...")

    if state.get("rag_sucesso"):
        print("Rag com sucesso, finalizando o fluxo.")
        return "ok"

    state_da_pergunta = (state["pergunta"] or "").lower()

    if any(k in state_da_pergunta for k in KEYWORDS_ABRIR_TICKET):
        print("Rag falhou, mas foram encontradas keywords de abertura de ticket. Abrindo...")
        return "chamado"

    print("Rag falhou, sem keywords, vou pedir mais informa√ß√µes...")
    return "info"

from langgraph.graph import StateGraph, START, END

workflow = StateGraph(AgentState)

workflow.add_node("triagem", node_triagem)
workflow.add_node("auto_resolver", node_auto_resolver)
workflow.add_node("pedir_info", node_pedir_info)
workflow.add_node("abrir_chamado", node_abrir_chamado)

workflow.add_edge(START, "triagem")
workflow.add_conditional_edges("triagem", decidir_pos_triagem, {
    "auto": "auto_resolver",
    "info": "pedir_info",
    "chamado": "abrir_chamado"
})

workflow.add_conditional_edges("auto_resolver", decidir_pos_auto_resolver, {
    "info": "pedir_info",
    "chamado": "abrir_chamado",
    "ok": END
})

workflow.add_edge("pedir_info", END)
workflow.add_edge("abrir_chamado", END)

grafo = workflow.compile()

from IPython.display import display, Image

graph_bytes = grafo.get_graph().draw_mermaid_png()
display(Image(graph_bytes))

